@charset "UTF-8";.block--messages .block-outer.block-outer--after{border:none}.community-list{margin-top:15px}.community-list .community-item{display:flex;flex-direction:row;margin-bottom:18px}.community-list .community-item:last-of-type{margin-bottom:0}.community-list .community-item .community-avatar-link{flex-basis:36px;flex-shrink:0;flex-grow:0;margin-right:9px}.community-list .community-item .community-avatar{width:36px;height:36px;border-radius:15%}.community-list .community-item .community-data{display:inline-block;width:100%;height:100%}.community-list .community-item .community-data:hover{cursor:pointer;text-decoration:none}.community-list .community-item .community-data:hover .community-title{text-decoration:underline}.community-list .community-item .community-info{width:83%;font-size:15px;display:flex;flex-direction:column;margin-right:18px;line-height:21px}.community-list .community-item .community-info .community-title{font-weight:500;margin-bottom:-2px;margin-top:-2px;white-space:nowrap;overflow:hidden;text-overflow:ellipsis}.community-list .community-item .community-info .community-member-count{color:#686c6e;font-size:14px}.community-list .community-item .forum-data{display:inline-flex;width:100%;align-items:center}.community-list .community-item .forum-data>*{margin-right:9px}.community-list .community-item .new-tag{background-color:#0f5257;color:#fff;font-weight:medium;font-size:9px;line-height:6px;letter-spacing:1px;width:39px;height:15px;text-align:center;border-radius:3px;padding-top:5px}.community-list .community-item .community-cta-button{flex-basis:64px;flex-shrink:0;flex-grow:0;height:36px}